import React, {Fragment, useState} from 'react';
require('dotenv').config({ path: '../../../.env' });
console.log('port2', process.env.port2);

const InputTodo = () => {
  const [description, setDescription] = useState('');

  const onSubmitForm = async (e) => {
    //need prevent default bc we dont want page to refresh
    e.preventDefault();
    try {
      const body = { description };
      const response = await fetch(`http://localhost:${process.env.port2}/todos`, {
        method: "POST",
        headers: { "Content-Type": "application/json"},
        body: JSON.stringify(body)
      });
      window.location = '/';
      console.log('Res', response);
    } catch (err) {
      console.error(err.message);
    }
  }
  return (
    <Fragment>
    <h1 className='text-center mt-5'>Your To-Do List</h1>
    <form className='d-flex mt-5' onSubmit={onSubmitForm}>
      <input type='text' className='form-control' value={description} onChange={e => setDescription(e.target.value)}/>
      <button className='btn btn-success'>Add</button>
    </form>
    </Fragment>
  )
}

export default InputTodo;
